from requests import request
from web3 import Web3
import json


class Blockchain():

    def __init__(self):
        self.infura_url = 'https://mainnet.infura.io/v3/ad9c316882ab4ef98d5868ac38209476'
        self.web3 = Web3(Web3.HTTPProvider(self.infura_url))
        self.connection = self.web3.isConnected()
        if (self.connection):
            print('\nRouter is Connected\n')
        else:
            print('\nRouter not connected]\n')

    def balance(self, address):
        self.address = address
        self.balance = self.web3.eth.getBalance(self.address)
        print('The address {} contains'.format(address),round(self.web3.fromWei(self.balance, 'ether'),4),'ETH\n')

    def blockinfo(self, blockVar = 'latest'):
        self.blockVar = blockVar
        block = self.web3.eth.get_block(self.blockVar)
        print('Here is the block info: \n',block)

    def transaction(self, transaction ):
        self.transaction = transaction
        self.transaction = self.web3.eth.getTransaction(self.transaction)
        print(self.transaction)
    
    def transaction_recipt(self, transaction):
        self.transaction = transaction
        self.recipt = self.web3.eth.getTransaction(self.transaction)
        print(self.recipt)

