userInput = (input('\nHi, please enter the number of what would you like to know today: \n'))


get = Blockchain()


if userInput == '1':
    account = input('Please enter a valid Ethereum address: ')
    get.balance(account)

elif userInput == '2':
    try:
        userblock = get.blockinfo(input("Please enter the block's address, else press enter: "))
        get.blockinfo(userblock)
    except ValueError:
        get.blockinfo()

elif userInput == '3':
    transaction = input('Please enter a valid transaction address: ')
    get.transaction(transaction)
    get.transaction_recipt(transaction)

elif userInput == '4':
    address = input("Please enter a valid ERC-20 Token: ")
    get.erc20(address)
